contract DDO {
    event newDDOEvent(
        string indexed did,
        bytes flags,
        bytes data
    );
    event updateDDOEvent(
        string indexed did,
        bytes flags,
        bytes data
    );

    constructor()
    public 
    {

    }

    function newDDO(
        string memory did,
        bytes memory flags,
        bytes memory data
    ) 
        public

    {
        emit newDDOEvent(did,flags,data);

    }

    function updateDDO(
        string memory did,
        bytes memory flags,
        bytes memory data
    ) 
        public

    {
        emit updateDDOEvent(did,flags,data);

    }
}
